import * as React from 'react';
import TextField from '@mui/material/TextField';
import { Container, Paper, makeStyles, Button } from '@material-ui/core';
import { Padding } from '@mui/icons-material';
//  import { createTheme, ThemeProvider } from '@material-ui/core';

// const theme = createTheme({
//   typography: {
//   fontFamily: [
//   'Pixelify Sans',
//    'cursive'
//    ].join(','),
//   }
// });
const useStyles = makeStyles((theme) => ({
  root: {
    '& > * ':{
      margin: theme.spacing(1),
    },
  },
}));


export default function Student() {
    const paperStyle = { padding: '50px 20px', width: 500, margin: "20px auto"}; 
    //setting the theme of the header
      const[name,setName]=React.useState('')   //useState allows to input and post text
      const[address,setAddress]=React.useState('')  
      const classes = useStyles();
    
      const handleClick=(e)=>{ //seting up a button
      e.preventDefault()
      const student={name, address}
      console.log(student);
      fetch("https://localhost/8080/student/add",{
        method:"POST",
        headers:{"Content-Type":"application/json"},
        body:JSON.stringfy(student)
      }

      )
  }    

  return (

    <Container>
      <Paper elevation={1} style={paperStyle}>
          <h1 style={{color:"red"}} ><u>Spring Boot and React</u></h1>

    <form className={classes.root} noValidate autoComplete= "Off">

      <TextField className='pixelName' id="standard-basic" label="Name" variant="standard" fullWidth 
      value={name} onChange={(e)=>setName(e.target.value)} //calling the useState setName
      />
      
      <TextField id="standard-basic" label="Address" variant="standard" fullWidth 
      value={address} onChange={(e)=>setAddress(e.target.value)}
      />
      <Button variant="contained" style={{color:"red"}} onClick={handleClick}>
        Sumbmit
      </Button>
      </form>
      {/* {name} {address} */}
      
    </Paper>
    </Container>

  );
}
