import * as React from 'react';
import TextField from '@mui/material/TextField';
import { Container, Paper, makeStyles } from '@material-ui/core';
import { Padding } from '@mui/icons-material';
import { createTheme, ThemeProvider } from '@material-ui/core';

const theme = createTheme({
  typography: {
  fontFamily: [
  'Pixelify Sans',
   'cursive'
   ].join(','),
  }
});
const useStyles = makeStyles((theme) => ({
  root: {
    '& > * ':{
      margin: theme.spacing(1),
    },
  },
}));

export default function Student() {
    const paperStyle = {padding: '50px 20px', width: 400, margin: "20px auto"};
    const[name,setName]=React.useState('')   
    const[address,setaddress]=React.useState('')  
    const classes = useStyles();

  return (
    <ThemeProvider theme={theme}>
    <Container>
      <Paper elevation={3} style={paperStyle}>
          <h1 style={{color:"red"}}><u>Add Student</u></h1>

    <form className={classes.root} noValidate autoComplete= "Off">

      <TextField id="standard-basic" label=" Name" variant="standard" fullWidth theme={theme} />
      
      <TextField id="standard-basic" label=" Address" variant="standard" fullWidth theme={theme}/>
      </form>
    </Paper>
    </Container>
    </ThemeProvider>
  );
}
